%Q[
    <td data-game="#{id}" data-value="#{name}">
      <a href="#" class="game-link" data-toggle="popover" data-title="#{name}">#{name}
      <div class="game-description">#{description}</div>
    </td>
    <td>#{year || '?'}</td>
    <td>#{genre || '?'}</td>
    <td>#{developer || '?'}</td>
  </tr>
]
